CREATE TABLE User_Accounts (UID INT(8) NOT NULL AUTO_INCREMENT, Email VARCHAR(100) NOT NULL, Password VARCHAR(128) NOT NULL, Status VARCHAR(20) NOT NULL, Type VARCHAR(20) NOT NULL, CreateDate timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, LastModified timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY(UID),UNIQUE(Email),INDEX name (UID,Email,CreateDate,LastModified));

CREATE TABLE Tools (TID INT(8) NOT NULL AUTO_INCREMENT, Tool_Num VARCHAR(100) NOT NULL, Tool_Name VARCHAR(100) NOT NULL, Locker_Num INT(8) NOT NULL, Status VARCHAR(20) NOT NULL, CreateDate timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, LastModified timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY(TID), INDEX name (TID,CreateDate,LastModified));

CREATE TABLE Rent_History (HID INT(8) NOT NULL AUTO_INCREMENT, TID INT(8) NOT NULL, Status VARCHAR(20) NOT NULL, CreateDate timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, LastModified timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY(HID), FOREIGN KEY(TID) REFERENCES Tools(TID),INDEX name (HID,CreateDate,LastModified))